INSERT INTO `sys_notice` VALUES ('060849d8c59f3816a0b1f353b09b5f08', '必须招标的工程项目规定', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">必须招标的工程项目规定</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2019-06-07</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">中华人民共和国国家发展和改革委员会令</strong></p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">第16号</strong></p>\n<p style=\"box-sizing: border-