INSERT INTO `sys_notice` VALUES ('3395499f4b68409fe14f0f7c76ac894d', '国家发展改革委关于印发投资咨询评估管理办法的通知（发改投资规〔2018〕1604号）', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">国家发展改革委关于印发投资咨询评估管理办法的通知（发改投资规〔2018〕1604号）</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2019-05-12</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">国家发展改革委关于印发</strong></p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">投资咨询评估管理办法的通知</strong></p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: right;\">发改投资规〔2018〕1604号</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">国务院各部委、各直属机构，各省、自治区、直辖市及计划单列市、新疆生产建设兵团发展改革委，各中央管理企业：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">为进一步完善投资决策程序，提高投资决策的科学性和民主性，规范投资决策过程中的咨询评估工作，保障咨询评估质量，我委对《国家发展改革委委托投资咨询评估管理办法（2015年修订）》进行了修订，制定了《国家发展改革委投资咨询评估管理办法》。现印发你们，请按照执行。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: right;\">国家发展改革委</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: right;\">2018年11月5日</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 50px 0px 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\"><span class=\"attachment\" contenteditable=\"false\"><a title=\"国家发展改革委投资咨询评估管理办法.pdf\" href=\"../webdisk/manager/filetransfer/downloadFiles?fileId=42258a82954142b6af344943a81f4f04\" target=\"_blank\" rel=\"noopener\">国家发展改革委投资咨询评估管理办法.pdf</a></span></div>', '1', '2022-05-21 08:33:39', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:33:39', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-24 06:34:01');
INSERT INTO `sys_notice` VALUES ('40c324f98d7a1107ee2ca6ef8553ef6e', '工业和信息化部关于规范稀土投资项目核准的指导意见', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">工业和信息化部关于规范稀土投资项目核准的指导意见</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2021-07-09</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; background: #f1f1f1;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; text-align: center;\">工业和信息化部关于规范稀土投资项目核准的指导意见</div>\n</div>\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px;\">&nbsp;</div>\n</div>\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">工信部原〔2017〕127号</p>\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />有关省（区、市）工业和信息化主管部门：<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 为深入贯彻落实党中央、国务院关于简政放权、放管结合、优化服务的总体决策部署，做好稀土矿山开发、冶炼分离和深加工项目核准职能下放后的指导工作，充分发挥行业规划、产业政策和规范管理等政策文件的规范引导作用，保护稀土战略资源，推动产业结构调整、转型升级，现就规范稀土投资项目核准提出如下意见。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 一、指导思想和总体要求<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 全面贯彻党的十八大和十八届三中、四中、五中、六中全会精神，落实深化行政体制改革、转变政府职能与&ldquo;放管服&rdquo;同时推进的总体要求，加强稀土战略资源保护，深入推进稀土行业供给侧结构性改革，实现稀土项目核准与行业规划、大型稀土集团组建、技术改造、规范管理等政策的密切结合，依法全面履行职能，严控新增稀土开采和冶炼分离产能，强化事中事后监管，促进稀土行业持续健康发展。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二、合理界定稀土项目核准范围<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （一）稀土矿山开发和冶炼分离项目核准范围界定。严格控制新增稀土矿山开发和冶炼分离产能，稀土核准项目包括新建和改扩建稀土矿山开发、冶炼分离项目（含稀土资源综合回收利用企业的冶炼分离生产线，下同）。根据《国务院关于促进稀土行业持续健康发展的若干意见》（国发〔2011〕12号）要求，不得审批6家稀土集团（中国稀有稀土股份有限公司、五矿稀土集团有限公司、中国北方稀土（集团）高科技股份有限公司、广东省稀土产业集团有限公司、中国南方稀土集团有限公司、厦门钨业股份有限公司）及其参控股企业以外的稀土矿山开发、冶炼分离项目（含利用境外资源建选矿厂、冶炼分离项目），新建项目应达到《稀土行业规范条件》要求。稀土冶炼分离项目须实施等量或减量置换，并向社会进行公告，坚决遏制产能盲目扩张。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （二）稀土资源回收项目核准范围界定。利用高速公路等基础设施建设中抢救性回收稀土矿的，按照稀土矿山开发项目核准；稀土资源综合回收利用项目中含有稀土冶炼分离生产线的，按稀土冶炼分离项目核准；利用稀土磁性材料等工业废料回收稀土，其中未新建冶炼分离生产线的项目，不在核准范围。禁止核准以&ldquo;资源综合利用&rdquo;为名变相加工稀土矿产品、冶炼分离产品的项目。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （三）稀土深加工项目核准范围界定。稀土深加工项目核准仅限于对稀土冶炼分离产品进一步加工生产稀土金属、各类稀土合金等加工项目。采用冶炼分离得到的稀土化合物（包括氧化物及盐类）、稀土金属及合金为原料生产稀土磁性、储氢、发光、催化、抛光、晶体等新材料及相关下游应用产品项目不纳入核准范围。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 三、严格审查稀土项目核准应具备条件<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （四）项目核准申请材料完备。申请项目核准的企业需按照《企业投资项目核准和备案管理条例》（国务院令第673号）要求，提供项目申请书和所在地城乡规划行政主管部门出具的选址意见书和国土资源行政主管部门出具的用地预审意见等证明文件，保证材料真实、可靠。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （五）符合国家稀土行业发展规划。行业规划是投资项目核准、备案的重要依据，有关省（区、市）要严格遵循工业和信息化部发布的《稀土行业发展规划》要求，结合相关资源、环保、安全等专项规划及本省（区、市）稀土行业发展规划，统筹研究确定项目建设的必要性和可行性，不得审批与行业发展规划及相关产业政策相违背的项目。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （六）加强与生产总量控制计划衔接。国家对稀土开采、生产实行计划管理。新建稀土矿山开发、冶炼分离项目（含利用境外进口稀土矿产品或混合稀土化合物等原料的项目）应取得国务院有关部门下达的稀土开采、生产总量控制计划指标，落实稀土原料来源。项目单位应建立稀土资源开发和生产经营台账，与稀土产品追溯体系做好衔接，节约利用资源，不得向无计划和违法违规企业销售或购买稀土矿产品。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （七）杜绝落后生产工艺。严禁核准产业政策中淘汰类的稀土项目，禁止采用国家明令淘汰的落后生产工艺、技术和装备。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 四、规范稀土项目核准工作<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （八）制定核准管理办法。根据国家行政审批制度改革和稀土行业管理总体要求，制定本地区稀土投资项目核准管理办法，细化项目核准原则和流程，规范行政审批行为，为企业做好服务。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （九）加快企业转型升级。综合考虑本地区稀土资源、技术储备等基础条件，引导企业转变发展思路，改变单纯扩大矿山开发、冶炼分离产能的模式，不断改造提升传统产能，大力发展稀土功能新材料及相关下游应用产品。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 五、加强项目建设监管<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;&nbsp;&nbsp;&nbsp; （十）强化事中事后监管。切实做好后续监管工作，要与环保、安监、节能等部门加强信息互联共享，实现协同监管，做到&ldquo;谁审批谁监管、谁主管谁监管&rdquo;，确保项目按照核准方案开工建设，严肃查处越权审批和违法建设行为，不断激发企业扩大合理有效投资和创新创业的活力。<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />&nbsp;\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　附件：<a style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; background-color: transparent; color: #000000; text-decoration-line: none; cursor: pointer;\" href=\"http://www.miit.gov.cn/n1146285/n1146352/n3054355/n3057569/n3057573/c6389227/part/6389264.doc\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; color: #3366ff;\">稀土项目核准涉及产品范围界定目录</span></a></p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: right;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />工业和信息化部<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\" />2017年7月3日</p>\n</div>\n</div>', '1', '2022-05-21 08:32:55', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:32:55', '', '2022-05-21 08:32:55');
INSERT INTO `sys_notice` VALUES ('454aad6a59225e1e6dbf39991fbd2d5d', '国家发展改革委关于印发《不单独进行节能审查的行业目录》的通知', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">国家发展改革委关于印发《不单独进行节能审查的行业目录》的通知</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2019-05-08</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">国家发展改革委关于印发</strong></p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">《不单独进行节能审查的行业目录》的通知</strong></p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">发改环资规〔2017〕1975号</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">中直管理局，教育部、工业和信息化部、财政部、住房城乡建设部、交通运输部、商务部、国资委、质检总局、统计局、银监会、国管局、能源局、粮食局，各省、自治区、直辖市及计划单列市、新疆生产建设兵团发展改革委，有关地区经信委（工信委、工信厅、经信局），浙江省能源局：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　为进一步深化&ldquo;放管服&rdquo;改革，根据《中华人民共和国节约能源法》《固定资产投资项目节能审查办法》（国家发展改革委2016年第44号令），现将《不单独进行节能审查的行业目录》印发给你们，并就有关事项通知如下。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　一、对于本目录中的项目，建设单位可不编制单独的节能报告，可在项目可行性研究报告或项目申请报告中对项目能源利用情况、节能措施情况和能效水平进行分析。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　二、节能审查机关对本目录中的项目不再单独进行节能审查，不再出具节能审查意见。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　三、建设单位投资建设本目录中的项目应按照相关节能标准、规范建设，采用节能技术、工艺和设备，加强节能管理，不断提高项目能效水平。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　四、各地节能管理部门应依据《中华人民共和国节约能源法》《固定资产投资项目节能审查办法》和《节能监察办法》（国家发展改革委2016年第33号令），对本目录中项目进行监督管理，对违反节能法律法规、标准规范的项目进行处罚。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　五、年综合能源消费量不满1000吨标准煤，且年电力消费量不满500万千瓦时的固定资产投资项目，以及涉及国家秘密的项目参照适用以上规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　六、本通知自印发之日起执行。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">国家发展改革委</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">2017年11月15日</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 50px 0px 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\"><span class=\"attachment\" contenteditable=\"false\"><a title=\"不单独进行节能审查的行业目录.pdf\" href=\"../webdisk/manager/filetransfer/downloadFiles?fileId=5d544f1517624fd99a56e35149ffe7be\" target=\"_blank\" rel=\"noopener\">不单独进行节能审查的行业目录.pdf</a></span></div>', '1', '2022-05-21 08:33:26', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:33:26', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 09:43:19');
INSERT INTO `sys_notice` VALUES ('54c2294a410eaaca8d50ac541c9e7492', '国家发展改革委 国家能源局关于促进智能电网发展的指导意见', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">国家发展改革委 国家能源局关于促进智能电网发展的指导意见</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2019-09-03</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">国家发展改革委 国家能源局关于促进智能电网发展的指导意见</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">详情见附件</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 50px 0px 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\"><a id=\"download\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; background-color: transparent; color: #0d83bf; cursor: pointer;\"></a>国家发展改革委+国家能源局关于促进智能电网发展的指导意见.docx</div>', '1', '2022-05-21 08:31:07', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:31:07', '', '2022-05-21 08:31:07');
INSERT INTO `sys_notice` VALUES ('59dc07c934b907f232a2d5559fed94ee', '企业投资项目核准和备案管理条例', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">企业投资项目核准和备案管理条例</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2019-09-02</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp; &nbsp; &nbsp; &nbsp;第一条　为了规范政府对企业投资项目的核准和备案行为，加快转变政府的投资管理职能，落实企业投资自主权，制定本条例。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第二条　本条例所称企业投资项目（以下简称项目），是指企业在中国境内投资建设的固定资产投资项目。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第三条　对关系国家安全、涉及全国重大生产力布局、战略性资源开发和重大公共利益等项目，实行核准管理。具体项目范围以及核准机关、核准权限依照政府核准的投资项目目录执行。政府核准的投资项目目录由国务院投资主管部门会同国务院有关部门提出，报国务院批准后实施，并适时调整。国务院另有规定的，依照其规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　对前款规定以外的项目，实行备案管理。除国务院另有规定的，实行备案管理的项目按照属地原则备案，备案机关及其权限由省、自治区、直辖市和计划单列市人民政府规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第四条　除涉及国家秘密的项目外，项目核准、备案通过国家建立的项目在线监管平台（以下简称在线平台）办理。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　核准机关、备案机关以及其他有关部门统一使用在线平台生成的项目代码办理相关手续。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　国务院投资主管部门会同有关部门制定在线平台管理办法。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第五条　核准机关、备案机关应当通过在线平台列明与项目有关的产业政策，公开项目核准的办理流程、办理时限等，并为企业提供相关咨询服务。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第六条　企业办理项目核准手续，应当向核准机关提交项目申请书；由国务院核准的项目，向国务院投资主管部门提交项目申请书。项目申请书应当包括下列内容：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（一）企业基本情况；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（二）项目情况，包括项目名称、建设地点、建设规模、建设内容等；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（三）项目利用资源情况分析以及对生态环境的影响分析；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（四）项目对经济和社会的影响分析。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　企业应当对项目申请书内容的真实性负责。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　法律、行政法规规定办理相关手续作为项目核准前置条件的，企业应当提交已经办理相关手续的证明文件。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第七条　项目申请书由企业自主组织编制，任何单位和个人不得强制企业委托中介服务机构编制项目申请书。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　核准机关应当制定并公布项目申请书示范文本，明确项目申请书编制要求。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第八条　由国务院有关部门核准的项目，企业可以通过项目所在地省、自治区、直辖市和计划单列市人民政府有关部门（以下称地方人民政府有关部门）转送项目申请书，地方人民政府有关部门应当自收到项目申请书之日起5个工作日内转送核准机关。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　由国务院核准的项目，企业通过地方人民政府有关部门转送项目申请书的，地方人民政府有关部门应当在前款规定的期限内将项目申请书转送国务院投资主管部门，由国务院投资主管部门审核后报国务院核准。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第九条　核准机关应当从下列方面对项目进行审查：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（一）是否危害经济安全、社会安全、生态安全等国家安全；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（二）是否符合相关发展建设规划、技术标准和产业政策；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（三）是否合理开发并有效利用资源；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（四）是否对重大公共利益产生不利影响。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　项目涉及有关部门或者项目所在地地方人民政府职责的，核准机关应当书面征求其意见,被征求意见单位应当及时书面回复。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　核准机关委托中介服务机构对项目进行评估的，应当明确评估重点；除项目情况复杂的，评估时限不得超过30个工作日。评估费用由核准机关承担。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十条　核准机关应当自受理申请之日起20个工作日内，作出是否予以核准的决定；项目情况复杂或者需要征求有关单位意见的，经本机关主要负责人批准，可以延长核准期限，但延长的期限不得超过40个工作日。核准机关委托中介服务机构对项目进行评估的，评估时间不计入核准期限。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　核准机关对项目予以核准的，应当向企业出具核准文件；不予核准的，应当书面通知企业并说明理由。由国务院核准的项目，由国务院投资主管部门根据国务院的决定向企业出具核准文件或者不予核准的书面通知。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十一条　企业拟变更已核准项目的建设地点，或者拟对建设规模、建设内容等作较大变更的，应当向核准机关提出变更申请。核准机关应当自受理申请之日起20个工作日内，作出是否同意变更的书面决定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十二条　项目自核准机关作出予以核准决定或者同意变更决定之日起2年内未开工建设，需要延期开工建设的，企业应当在2年期限届满的30个工作日前，向核准机关申请延期开工建设。核准机关应当自受理申请之日起20个工作日内，作出是否同意延期开工建设的决定。开工建设只能延期一次，期限最长不得超过1年。国家对项目延期开工建设另有规定的，依照其规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十三条　实行备案管理的项目，企业应当在开工建设前通过在线平台将下列信息告知备案机关：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（一）企业基本情况；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（二）项目名称、建设地点、建设规模、建设内容；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（三）项目总投资额；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　（四）项目符合产业政策的声明。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　企业应当对备案项目信息的真实性负责。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　备案机关收到本条第一款规定的全部信息即为备案；企业告知的信息不齐全的，备案机关应当指导企业补正。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　企业需要备案证明的，可以要求备案机关出具或者通过在线平台自行打印。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十四条　已备案项目信息发生较大变更的，企业应当及时告知备案机关。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十五条　备案机关发现已备案项目属于产业政策禁止投资建设或者实行核准管理的，应当及时告知企业予以纠正或者依法办理核准手续，并通知有关部门。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十六条　核准机关、备案机关以及依法对项目负有监督管理职责的其他有关部门应当加强事中事后监管，按照谁审批谁监管、谁主管谁监管的原则，落实监管责任，采取在线监测、现场核查等方式，加强对项目实施的监督检查。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　企业应当通过在线平台如实报送项目开工建设、建设进度、竣工的基本信息。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十七条　核准机关、备案机关以及依法对项目负有监督管理职责的其他有关部门应当建立项目信息共享机制，通过在线平台实现信息共享。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　企业在项目核准、备案以及项目实施中的违法行为及其处理信息，通过国家社会信用信息平台向社会公示。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十八条　实行核准管理的项目，企业未依照本条例规定办理核准手续开工建设或者未按照核准的建设地点、建设规模、建设内容等进行建设的，由核准机关责令停止建设或者责令停产，对企业处项目总投资额1&permil;以上5&permil;以下的罚款；对直接负责的主管人员和其他直接责任人员处2万元以上5万元以下的罚款，属于国家工作人员的，依法给予处分。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　以欺骗、贿赂等不正当手段取得项目核准文件，尚未开工建设的，由核准机关撤销核准文件，处项目总投资额1&permil;以上5&permil;以下的罚款；已经开工建设的，依照前款规定予以处罚；构成犯罪的，依法追究刑事责任。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第十九条　实行备案管理的项目，企业未依照本条例规定将项目信息或者已备案项目的信息变更情况告知备案机关，或者向备案机关提供虚假信息的，由备案机关责令限期改正；逾期不改正的，处2万元以上5万元以下的罚款。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第二十条　企业投资建设产业政策禁止投资建设项目的，由县级以上人民政府投资主管部门责令停止建设或者责令停产并恢复原状，对企业处项目总投资额5&permil;以上10&permil;以下的罚款；对直接负责的主管人员和其他直接责任人员处5万元以上10万元以下的罚款，属于国家工作人员的，依法给予处分。法律、行政法规另有规定的，依照其规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第二十一条　核准机关、备案机关及其工作人员在项目核准、备案工作中玩忽职守、滥用职权、徇私舞弊的，对负有责任的领导人员和直接责任人员依法给予处分；构成犯罪的，依法追究刑事责任。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第二十二条　事业单位、社会团体等非企业组织在中国境内投资建设的固定资产投资项目适用本条例，但通过预算安排的固定资产投资项目除外。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第二十三条　国防科技工业企业在中国境内投资建设的固定资产投资项目核准和备案管理办法，由国务院国防科技工业管理部门根据本条例的原则另行制定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　第二十四条　本条例自2017年2月1日起施行。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n</div>', '1', '2022-05-21 08:31:47', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:31:47', '', '2022-05-21 08:31:47');
INSERT INTO `sys_notice` VALUES ('5a5bd0abc4704b353948f964b31f3821', '山西省自然资源厅2021年第六十七批 “矿山开发治理”方案评审结果的公告', '2', '<p class=\"MsoNormal\" style=\"margin: 0px 0px 0px 108.4pt; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: -108.4pt; text-align: center; line-height: 25pt;\" align=\"center\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; list-style: none;\">关于自然资源厅在</strong><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; list-style: none;\">政务服务网公</strong><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; list-style: none;\">告</strong></p>\n<p class=\"MsoNormal\" style=\"margin: 0px 0px 0px 108.4pt; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: -108.4pt; text-align: center; line-height: 25pt;\" align=\"center\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; list-style: none;\">&ldquo;矿山开发治理&rdquo;方案评审结果的函</strong></p>\n<p class=\"MsoNormal\" style=\"margin: 0px 0px 0px 96pt; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: -96pt; line-height: 25pt;\">&nbsp;</p>\n<p class=\"MsoNormal\" style=\"margin: 0px 0px 0px 96pt; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: -96pt; line-height: 25pt;\">省政务服务中心：</p>\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: 32pt; line-height: 25pt;\">我厅对山西忻州神达惠安煤业有限公司、山西龙泰民源矿业有限责任公司、山西阳泉郊区神堂煤业有限公司等3<span style=\"padding: 0px; box-sizing: border-box; list-style: none; margin: 0px 0px 0px !important 0px;\">座矿山</span>&ldquo;矿山开发治理&rdquo;方案进行了公示。</p>\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: 32pt; line-height: 25pt;\">公示结果无异议，我厅决定予以公告。请中心给予支持。</p>\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: 32pt; line-height: 25pt;\">附：<span style=\"padding: 0px; box-sizing: border-box; list-style: none; margin: 0px 0px 0px !important 0px;\">山西省自然资源厅</span>2021年第六十七批&ldquo;矿山开发治理&rdquo;方案评审结果的公告</p>\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; line-height: 25pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: 144pt; line-height: 25pt;\">山西省自然资源厅行政审批管理处</p>\n<p class=\"MsoNormal\" style=\"margin: 0px 0px 0px 63pt; padding: 0px; font-family: 微软雅黑; box-sizing: border-box; color: #333333; font-size: 16px; list-style: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); background-color: #ffffff; text-indent: 152pt; line-height: 25pt;\">2021年10月26日</p>', '1', '2022-05-21 08:24:52', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:24:52', '', '2022-05-21 08:24:52');
INSERT INTO `sys_notice` VALUES ('5b2e5bfd935c8cb6f9f6590d6a6601e0', '山西省企业投资项目核准和备案管理办法', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">山西省企业投资项目核准和备案管理办法</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2019-06-09</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">山西省企业投资项目核准和备案管理办法</strong></p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">山西省人民政府令</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">第258号</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">《山西省企业投资项目核准和备案管理办法》业经2018年11月21日省人民政府第17次常务会议通过，现予公布，自2019年1月1日起施行。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: right;\">省长 楼阳生</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: right;\">2018年12月4日</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">第一章 总则</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第一条 为了规范政府对企业投资项目的核准和备案行为，落实企业投资自主权，优化营商环境，根据《行政许可法》《企业投资项目核准和备案管理条例》等法律、行政法规，结合本省实际，制定本办法。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二条 本办法所称企业投资项目(以下简称项目)，是指企业在本省行政区域内投资建设的固定资产投资项目，包括企业使用自筹资金的项目，以及使用自筹资金并申请使用政府投资补助或者贷款贴息等的项目。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">项目申请使用政府投资补助或者贷款贴息的，应当在履行核准或者备案手续后提出资金申请报告。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三条 县级以上人民政府投资主管部门为项目核准、备案机关，对项目履行核准、备案管理职责。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">前款所称投资主管部门是指省、设区的市、县级发展和改革主管部门，具有企业技术改造投资管理职能的工业和信息化主管部门。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四条 关系国家安全、涉及国家和本省重大生产力布局、战略性资源开发和重大公共利益等项目，实行核准管理。其他项目实行备案管理。核准、备案机关及其他行政机关不得非法干预企业的投资自主权。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第五条 实行核准管理的具体项目范围以及核准机关、权限，由省人民政府制定的《山西省政府核准的投资项目目录》（以下简称《核准目录》）确定。《核准目录》由省发展和改革主管部门会同省有关部门研究提出，报省人民政府批准后公布实施，并可适时予以调整。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">法律、行政法规和国务院对项目核准的范围、权限有专门规定的，从其规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第六条《核准目录》以外的项目，按照下列权限实行备案管理：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（一）国家明确要求省级备案的项目以及跨设区的市行政区域的项目，由省级投资主管部门备案；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（二）省级投资主管部门明确要求设区的市备案的项目以及跨县级行政区域的项目，由设区的市级投资主管部门备案；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（三）设区的市所辖区的项目，备案权限由设区的市人民政府确定；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（四）其他项目按照属地原则由县（市）投资主管部门备案。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第七条 核准机关应当公开服务指南，列明项目核准的申报材料及所需附件、受理方式、审查条件、办理流程、办理时限等。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">备案机关应当列明项目备案所需信息内容、办理流程等。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第八条 除涉及国家秘密的项目外，项目核准、备案通过投资项目在线审批监管平台（以下简称在线平台）实行网上受理、办理、监管和服务，实现核准、备案过程和结果的可查询、可监督。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第九条 通过在线平台申报项目时，生成整个建设周期身份标识的唯一项目代码。核准、备案机关以及其他各有关部门统一使用该项目代码办理相关手续。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十条 企业投资建设项目，应当遵守国家法律、法规，符合国民经济和社会发展规划、专项规划、区域规划、产业政策、市场准入标准、资源开发、能耗与环境管理等要求，依法办理核准、备案以及其他相关手续，并如实提供相关材料，报告相关信息。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十一条 核准机关对项目进行的核准是行政许可事项。备案机关对项目进行的备案不是行政许可事项，备案机关仅对企业是否依法履行投资项目信息告知义务进行管理。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">实施核准所需的评估、评议等经费应当列入本级财政预算。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">第二章 项目核准</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十二条 企业办理项目核准手续，应当向核准机关提交项目申请报告。项目申请报告应当载明下列内容：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（一）企业基本情况；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（二）拟建项目情况，包括项目名称、建设地点、建设规模、投资规模、建设内容、建设工期等；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（三）资源利用情况分析、生态环境影响分析；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（四）经济和社会影响分析。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十三条 项目申请报告应当按照国务院投资主管部门发布的通用文本规定编写。任何单位和个人不得强制企业委托中介服务机构编制项目申请报告。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十四条 企业报送项目申请报告时，应当附具自然资源行政主管部门核发的选址意见书（仅指以划拨方式提供国有土地使用权的项目）、用地预审意见以及法律、行政法规规定需要办理的其他相关手续。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">在土地利用总体规划确定的城镇建设用地范围内，不涉及新增建设用地，使用已批准建设用地进行建设的项目，不再进行用地预审，由设区的市自然资源行政主管部门出具说明。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">企业对项目申请报告以及依法应当附具文件的真实性、合法性和完整性负责。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十五条 根据国家和本省《核准目录》规定，省、设区的市、县级投资主管部门核准的项目，企业可以直接向核准机关报送项目申请报告，也可以通过项目所在地投资主管部门转送项目申请报告。具体办法由省级核准机关制定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">国务院投资主管部门、国务院行业管理部门核准的项目，可以分别通过省政府投资主管部门、省政府行业管理部门转送项目申请报告。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">国务院核准的项目，由省政府投资主管部门向国务院投资主管部门转送项目申请报告。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">新建运输机场项目，由省人民政府向国务院、中央军委报送项目申请报告。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">转送项目申请报告的投资主管部门、行业管理部门，应当在收到项目申请书之日起5个工作日内转送核准机关。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十六条 项目申报材料齐全、符合法定形式的，核准机关应当予以受理。申报材料不齐全或者不符合法定形式的，核准机关应当在收到申报材料之日起3个工作日内一次性告知企业补充相关文件，或者对相关内容进行调整。逾期不告知的，自核准机关收到项目申报材料之日起即为受理。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">核准机关予以受理或者不予受理申报材料，都应当出具加盖本机关专用印章并注明日期的书面凭证或者通过在线平台出具电子凭证。予以受理的，应当注明项目代码，企业可以根据项目代码在线查询、监督核准过程和结果。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">核准机关应当按照《企业投资项目核准和备案管理条例》第九条对项目进行审查。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十七条 核准机关在正式受理项目申请报告后，需要评估的，应当在4个工作日内委托专业工程咨询机构进行评估。工程咨询机构应当在核准机关规定时间内提出评估报告，并对评估结论承担责任。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">核准机关委托工程咨询机构评估时，应当通过竞争性方式予以确定。受委托评估的工程咨询机构与编制项目申请报告的工程咨询机构为同一单位，或者存在控股、管理、负责人为同一人等重大关联关系的，不得承担该项目的评估工作。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">评估时限一般不得超过20个工作日。项目情况复杂的，经核准机关同意后，评估时限可以再延长20个工作日。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十八条 项目涉及有关行业管理部门或者项目所在地人民政府职责的，核准机关应当书面征求意见。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">行业管理部门或者项目所在地人民政府应当在7个工作日内出具书面审查意见，逾期未反馈的，视为同意。对重大项目，经核准机关同意后，审查时限可以延长7个工作日。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第十九条 项目建设可能对公众利益构成重大影响的，核准机关应当通过同级人民政府门户网站或者核准机关门户网站等征求公众意见。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十条 对于特别重大的项目，核准机关可以组织专家进行评议。除项目情况特别复杂外，专家评议时限不得超过20个工作日。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十一条 核准机关可以根据评估意见、审查意见、公众意见、专家评议意见等，要求企业对相关内容进行调整，或者对有关情况和文件作进一步说明、补充。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十二条 核准机关应当在正式受理申报材料后15个工作日内作出是否予以核准的决定。项目情况复杂的，经核准机关主要负责人批准，核准时限可以延长20个工作日。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">对项目进行评估或者专家评议所需时间不计算在前款规定的期限内。核准机关应当将评估或者专家评议所需时间告知企业。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十三条 项目符合核准条件的，核准机关应当予以核准并向企业出具核准文件。项目不符合核准条件的，核准机关应当出具不予核准的书面通知，并说明理由。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">核准机关出具核准文件或者不予核准的书面通知应当抄送同级行业管理、自然资源、水行政管理、生态环境、节能审查、文物等相关主管部门和下一级核准机关。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">核准机关应当将核准文件或者不予核准的书面通知，与项目申请报告、评估报告等文件资料一并存档备查。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十四条 取得核准文件的项目，有下列情形之一的，企业应当向原核准机关提出变更申请。原核准机关自受理申请之日起15个工作日内作出是否同意变更的决定：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（一）建设地点拟发生变更的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（二）项目法人拟发生变更的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（三）建设规模、建设内容拟发生较大变化的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（四）项目变更可能对经济、社会、环境等产生重大不利影响的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（五）对核准文件所规定的其他内容拟进行较大调整的。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十五条 经核准的项目变更后，需要重新核准的，企业应当向有权核准机关重新申请核准，原核准文件予以撤销。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">经核准的项目变更后，属于备案管理的，企业应当按照备案程序办理，原核准文件予以撤销。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十六条 项目自核准机关出具核准文件或者同意变更决定2年内未开工建设，需要延期开工建设的，企业应当在2年期限届满的30个工作日前，向核准机关申请延期开工建设。核准机关应当自受理申请之日起15个工作日内，作出是否同意延期开工建设的决定。开工建设只能延期一次，期限不得超过1年。国家对项目延期开工建设另有规定的，依照其规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">在2年期限内未开工建设也未按照规定向核准机关申请延期的，或者经核准机关同意延期期限内未开工建设的，核准文件自动失效。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">第三章 项目备案</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十七条 实行备案管理的项目，企业应当在开工建设前通过在线平台将基本信息告知备案机关，依法履行投资项目信息告知义务，并遵循诚信和规范原则。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十八条 省级投资主管部门应当制定项目备案基本信息格式文本。具体包括下列内容：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（一）企业基本情况；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（二）项目名称、建设地点、建设规模、建设内容、建设工期；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（三）项目总投资额；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（四）项目符合产业政策声明。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">企业应当对项目备案信息的真实性、合法性和完整性负责。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第二十九条 备案机关收到本办法第二十八条规定的全部信息后，应当在5个工作日内通过在线平台告知企业是否符合备案要求。符合备案要求，企业需要备案证明的，可以通过在线平台自行打印或者要求备案机关出具。备案证明格式文本由省级投资主管部门统一制定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">不符合备案要求的下列情形，备案机关应当告知、指导企业补正、纠正或者依法申请办理相关手续：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（一）项目备案信息不完整的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（二）项目属于产业政策禁止投资建设的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（三）项目依法应当实行核准管理的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（四）不属于固定资产投资项目的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（五）不属于本备案机关权限的。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十条 项目备案后，项目法人、建设地点发生变化或者建设规模、内容发生较大变更，企业应当重新办理备案手续。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">备案项目变更后属于核准管理的，企业应当按照核准程序办理，原备案证明予以撤销。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十一条 备案类项目推行承诺制管理。县级以上人民政府相关部门按照省人民政府确定的承诺制改革方案，办理项目报建审批手续。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">第四章 监督管理</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十二条 投资主管部门应当对项目开工前是否依法取得核准文件或者办理备案手续，开工后是否按照核准文件或者备案内容建设，进行事中事后监督管理。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">依法对项目负有监督管理责任的行业管理、自然资源、水行政管理、生态环境、节能审查、应急管理、审计、文物等部门，应当按照谁审批谁监管、谁主管谁监管的原则，采取在线监测、现场核查等方式，在各自职责范围内依法加强对项目实施的监督管理。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十三条 投资主管部门按照分级分类原则进行监管。对项目是否依法取得核准文件或者办理备案手续，由项目所在地县级核准、备案机关实施监督管理。对已经取得核准文件的项目，由核准机关实施监督管理；对已经备案的项目，由备案机关实施监督管理。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">上级核准、备案机关应当加强对下级核准、备案机关的指导和监督，及时纠正项目管理中存在的问题。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">核准、备案机关对企业违法违规行为进行行政处罚时，可以委托下级核准、备案机关实施。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十四条 投资主管部门以及依法对项目实施负有监督管理责任的其他部门，应当建立项目信息共享机制，将相关手续办理信息、审批结果信息、招标投标信息、监管（处罚）信息等，统一汇集至项目代码，通过在线平台实现互通共享。在线平台应当与省&ldquo;互联网+政务服务&rdquo;平台、省信用信息共享平台、省招标投标公共服务平台等平台实现对接。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十五条 投资主管部门及其工作人员不得擅自增减项目核准、备案的审查条件，不得超出办理时限。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">对不符合法定条件核准、备案的，或者超越法定职权核准、备案的，应当依法撤销。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">任何单位和个人都有权对项目核准或者备案、建设实施过程中的违法违规行为向有关部门举报。有关部门应当及时核实、处理。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十六条 企业应当履行项目管理主体责任。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">企业应当通过在线平台如实报送项目开工建设、建设进度、资金使用、竣工的基本信息。项目开工前，企业应当报备项目开工基本信息。项目开工后，企业应当按季度报备项目建设动态进度基本信息。项目竣工验收后，企业应当报备项目竣工基本信息。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十七条 企业有下列行为之一的，相关信息列入项目异常信用记录，并纳入省信用信息共享平台：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（一）应申请办理项目核准但未依法取得核准文件的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（二）提供虚假项目核准或者备案信息，或者未依法将项目信息告知备案机关，或者已备案项目信息变更未告知备案机关的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（三）违反法律法规擅自开工建设的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（四）不按照核准、备案内容建设的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（五）企业未按本办法第三十六条规定报送基本信息，或者报送虚假信息的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（六）其他违法违规行为。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">第五章 法律责任</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十八条 企业投资项目核准和备案工作中，违反本办法规定，法律、行政法规已有法律责任规定的，从其规定。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第三十九条 核准、备案机关及其工作人员，其他相关部门及其工作人员，在项目核准、备案以及相关审批手续办理中玩忽职守、滥用职权、徇私舞弊、索贿受贿的，对负有领导责任、直接责任的人员依法给予处分；构成犯罪的，依法追究刑事责任。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四十条 核准、备案机关有下列情形之一的，由其上级行政机关责令改正，对负有领导责任、直接责任的人员由有关单位和部门依纪依法给予处分：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（一）超越法定职权予以核准或者备案的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（二）对不符合法定条件的项目予以核准的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（三）对符合法定条件的项目不予核准的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（四）擅自增减核准审查条件的，或者以备案名义变相核准的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（五）不在法定期限内作出核准决定的；</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">（六）不依法履行监管职责或者监管不力，造成严重后果的。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四十一条 企业以隐瞒有关情况或者提供虚假申报材料等不正当手段申请核准、备案的，核准、备案机关不予受理或者不予核准、备案，并给予警告。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四十二条 工程咨询机构在受核准机关委托开展评估过程中，违反从业规定，造成不良影响的，由县级以上发展和改革主管部门责令改正并给予警告；造成重大损失和恶劣影响的，从工程咨询机构备案名录中移除，已获得资信评价等级的，取消其评价等级；构成犯罪的，依法追究刑事责任。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四十三条 工程咨询机构人员及专家在受核准机关委托开展评估、评议过程中，违反从业规定，造成不良影响的，对直接责任人员，由县级以上投资主管部门责令改正并给予警告；涉及技术人员的，由行业协会给予警告并通报批评，情节严重的，取消相关职业资格；构成犯罪的，依法追究刑事责任。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">第六章 附则</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四十四条 开发区管理委员会经依法授权，履行项目核准、备案管理职责。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四十五条 事业单位、社会团体等非企业组织在本省行政区域内利用自筹资金、不申请政府直接投资建设的固定资产投资项目，适用本办法。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">个人投资建设项目参照本办法的相关规定执行。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">第四十六条 本办法自2019年1月1日起施行。《山西省企业投资项目核准暂行办法》（山西省人民政府令第185号）、《山西省企业投资项目备案暂行办法》（山西省人民政府令第186号）同时废止。</p>\n</div>', '1', '2022-05-21 08:32:05', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:32:05', '', '2022-05-21 08:32:05');
