INSERT INTO `sys_notice` VALUES ('f9a8cc240ca1b0433c7ffca84b5cd010', '国家发展改革委办公厅关于印发《重大建设项目 领域基层政务公开标准指引》的通知', '2', '<h1 style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 1.1; font-size: 18px; font-family: 微软雅黑; font-weight: normal; color: #4386e2; text-align: center; background-color: #ffffff;\">国家发展改革委办公厅关于印发《重大建设项目 领域基层政务公开标准指引》的通知</h1>\n<div class=\"article-remark\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px; list-style-type: none; line-height: 40px; font-size: 14px; font-family: 微软雅黑; border: 0px; background: #e8eff5; color: #333333; text-align: center;\">\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; border: 0px; display: inline-block;\">发布日期：<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px;\">2019-09-03</span></div>\n</div>\n<div class=\"article-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; font-size: 14px; font-family: 微软雅黑; border: 0px; color: #949494; background-color: #ffffff;\">\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">各省、自治区、直辖市及计划单列市、新疆生产建设兵团发展改革委：</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">　　为进一步推进重大建设项目领域基层政务公开标准化规范化，提升基层政务公开和政务服务水平，按照国办信息公开办要求，我们编制了<a style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; line-height: 25px; background-color: transparent; color: #4d4d4d; text-decoration-line: none; cursor: pointer;\" href=\"http://www.ndrc.gov.cn/gzdt/201907/W020190704322106484100.pdf\" target=\"_blank\" rel=\"noopener\">《重大建设项目领域基层政务公开标准指引》</a>。现印发给你们，请参照执行。</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">国家发展改革委办公厅</p>\n<p style=\"box-sizing: border-box; margin: 10px 0px; padding: 0px; list-style-type: none; line-height: 25px; text-indent: 30px; text-align: center;\">2019年5月27日</p>\n</div>', '1', '2022-05-21 08:33:09', '', '0', 'ADE77F11422C401C8A3F09FF2A82CFE8', '2022-05-21 08:33:09', '', '2022-05-21 08:33:09');
